    private void jButton4ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton4ActionPerformed
        // TODO add your handling code here:
        try{
        String tf1 = jTextField1.getText();
        String tf2 = jTextField2.getText();
        double c1 = Double.valueOf(tf1);
        double c2 = Double.valueOf(tf2);
        if (c1==0 || c2==0) {
            jLabel1.setText("chyba: zadaj cislo");
        }
        else {
            Double v = c1 / c2;
            jLabel1.setText(v.toString());
        }
        } catch (Exception e){
        jLabel1.setText("zadaj cislo");
        }
    }//GEN-LAST:event_jButton4ActionPerformed

    private void jButton6ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton6ActionPerformed
        // TODO add your handling code here:
        try{
        String tf1 = jTextField1.getText();
            double c1 = Double.valueOf(tf1);
            Double v = ((c1*Math.PI) / 180);
            v = Math.sin(v);
            jLabel1.setText(v.toString());}
        catch(Exception e){
        jLabel1.setText("chyba: zadaj cislo");}
    }//GEN-LAST:event_jButton6ActionPerformed

