    private void jButton5ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton5ActionPerformed
        // TODO add your handling code here:
        try{
            String tf1 = jTextField1.getText();
            String tf2 = jTextField2.getText();
            double c1 = Double.valueOf(tf1);
            double c2 = Double.valueOf(tf2);
            double y = Math.pow(10,c2);
            c1 = Math.round(c1*y);
            Double v = (c1/y);
            jLabel1.setText(v.toString());}
        catch(Exception e){
        jLabel1.setText("chyba: zadaj cislo");}
    }//GEN-LAST:event_jButton5ActionPerformed

    private void jButton9ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton9ActionPerformed
        // TODO add your handling code here:
        try{
            String tf1 = jTextField1.getText();
            String tf2 = jTextField2.getText();
            double c1 = Double.valueOf(tf1);
            double c2 = Double.valueOf(tf2);
            if(c1 < c2){
                double c = Math.random()*100;
                c1 = c%(c2-c1)+c1;
                jLabel1.setText(String.valueOf(c1));
            } else {
                jLabel1.setText("Cislo 1 musi byt mensie nez cislo 2");
            }
        }
        catch(Exception e){
            jLabel1.setText("chyba: zadaj cislo");
        }
    }//GEN-LAST:event_jButton9ActionPerformed

